import axios from "axios";
import config from "@/config.json";

const isAuthenticated = () => {
    console.log(window.$cookies.get("jwt"))
  axios
    .get(`${config.BASE_URL}/login`, {
      headers: {
        Authorization: `Bearer ${window.$cookies.get("jwt")}`,
      },
    })
    .then((response) => {
      if (response.data.auth) return true;
      else return false;
    })
    .catch((err) => {
      throw err;
    });
};

export default isAuthenticated;
